package com.meadetechnologies.timetrackingapp.ui.employeelist

import android.content.Intent
import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import android.widget.TextView
import androidx.recyclerview.widget.RecyclerView
import com.bumptech.glide.Glide
import com.meadetechnologies.timetrackingapp.R
import com.meadetechnologies.timetrackingapp.data.model.Employee
import com.meadetechnologies.timetrackingapp.ui.employeedetail.EmployeeDetailActivity

class EmployeeAdapter(private val employees: List<Employee>) :
    RecyclerView.Adapter<EmployeeAdapter.EmployeeViewHolder>() {

    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): EmployeeViewHolder {
        val view = LayoutInflater.from(parent.context)
            .inflate(R.layout.list_item_employee, parent, false)
        return EmployeeViewHolder(view)
    }

    override fun onBindViewHolder(holder: EmployeeViewHolder, position: Int) {
        val employee = employees[position]
        holder.itemView.findViewById<TextView>(R.id.employeeNameTextView).text = employee.name
        //holder.itemView.employeeNameTextView.text = employee.name
        Glide.with(holder.itemView.context).load(employee.image ?: "data:image/png;base64,"
        ).into(holder.itemView.findViewById(R.id.imageView))

        holder.itemView.setOnClickListener {
            val intent = Intent(holder.itemView.context, EmployeeDetailActivity::class.java)
            intent.putExtra("employeeId", employee.id)
            holder.itemView.context.startActivity(intent)
        }
    }

    override fun getItemCount(): Int {
        return employees.size
    }

    class EmployeeViewHolder(itemView: View) : RecyclerView.ViewHolder(itemView) {
//        val employeeNameTextView = itemView.findViewById<TextView>(R.id.employeeNameTextView)
    }
}

